import react, { Component } from 'react'
import emailjs from 'emailjs-com'

import img1 from '../img/contactinfo.svg'

import discord from '../img/discord.svg'
import gmail from '../img/gmail.svg'
import facebook from '../img//facebook.svg'



class Form extends Component{


  state={
    popconfirm:0
  }
  rem=()=>{
    this.setState({popconfirm:0})
  }
       SendMail =(e)=>{
        e.preventDefault();


        if(e.target.nombre.value.length > 3  && e.target.apellido.value.length > 3 && e.target.correo.value.length > 3){
          emailjs.sendForm('service_thn34om', 'template_i2fjxsl', e.target, 'user_Tm5AttXYzU5BuRTJWijHT')
              .then((result) => {
                  console.log(result.text);
                  this.setState({popconfirm:1})
              }, (error) => {
                  console.log(error.text);
              });
              e.target.reset()
        
        
          
        }else{
         
        }
  

         
      }

      
      render(){
        let close1 ={
          transform:`scale(${this.state.popconfirm})`,
          transition:'all 1s'
        }
        return(
          <div>
          <div class="containerform" >
 <div>

   <form  method="POST" id="contact" onSubmit={this.SendMail}>
   <span>
       <p>Nombre</p>
       <input type="text" placeholder="Juan Carlos" name="nombre" className="nombre" required/>
     </span>



     <span>
       <p>Apellidos</p>
       <input type="text" placeholder="Ramirez" name="apellido" className="apellido" required/>
     </span>

     <span>
       <p>Correo</p>
       <input type="text" placeholder="Nestormyriee@gmail.com" name="correo" className="correo " required/>
     </span>
     <span>
       <p>Numero</p>
       <input type="text"   name="numero" className="numero" placeholder='opcional'/>
     </span>

     <span className="mensajecontent" required>
       <p>Mensaje</p>
       <textarea  className='mensaje'name="mensaje"  placeholder="Cuentame Tu priyecto (Opcional)"></textarea>
     </span>
     <input type='submit' value="Enviar" className="enviar" />
   </form>
 </div>
 <div>
   <img src={img1} alt=""/>
   <div class="anclas">
   <a href='https://discord.gg/TkMRSwuqMT'>   <img src={discord} alt=""/></a>
     <img src={gmail} alt=""/>
     <img src={facebook} alt=""/>
   </div>
 </div>
 <div className='success'  style={close1} onClick={this.rem}>
 <i className="far fa-times-circle close" ></i>
