import react, { Component } from 'react'
import emailjs from 'emailjs-com'

import img1 from '../img/contactinfo.svg'

import discord from '../img/discord.svg'
import gmail from '../img/gmail.svg'
import facebook from '../img//facebook.svg'

//import recaptcha from "react-google-recaptcha-v3"
import {ReCaptcha} from 'react-recaptcha-google'

class ExampleComponent extends Component {
  constructor(props, context) {
    super(props, context);
    this.onLoadRecaptcha = this.onLoadRecaptcha.bind(this);
    this.verifyCallback = this.verifyCallback.bind(this);
  }
  componentDidMount() {
    if (this.captchaDemo) {
        console.log("started, just a second...")
        this.captchaDemo.reset();
        this.captchaDemo.execute();
    }
  }
  onLoadRecaptcha() {
      if (this.captchaDemo) {
          this.captchaDemo.reset();
          this.captchaDemo.execute();
      }
  }
  verifyCallback(recaptchaToken) {
    // Here you will get the final recaptchaToken!!!  
    console.log(recaptchaToken, "<= your recaptcha token")
  }
  render() {
    return (
      <div>
        {/* You can replace captchaDemo with any ref word */}
        <ReCaptcha
            ref={(el) => {this.captchaDemo = el;}}
            size="invisible"
            render="explicit"
            sitekey="6LdB6XQaAAAAAFTOn3IpqLTHnoi2msKnRFxmwKog"
            onloadCallback={this.onLoadRecaptcha}
            verifyCallback={this.verifyCallback}
        />
        <code>
          1. Add <strong>your site key</strong> in the ReCaptcha component. <br/>
          2. Check <strong>console</strong> to see the token.
        </code>
      </div>
    );
  };
};
class Form extends Component{


  

  state={
    popconfirm:0
  }



  
  rem=()=>{
    this.setState({popconfirm:0})
  }
       SendMail =(e)=>{
        e.preventDefault();


        if(e.target.nombre.value.length > 3  && e.target.apellido.value.length > 3 && e.target.correo.value.length > 3){
          emailjs.sendForm('service_thn34om', 'template_i2fjxsl', e.target, 'user_Tm5AttXYzU5BuRTJWijHT')
              .then((result) => {
                  console.log(result.text);
                  this.setState({popconfirm:1})

                  localStorage.setItem()
              }, (error) => {
                  console.log(error.text);
              });
              e.target.reset()
        
        
          
        }
  

         
      }

      
      render(){
        let close1 ={
          transform:`scale(${this.state.popconfirm})`,
          transition:'all 1s'
        }
        return(
          <div>
          <div class="containerform" >
 <div>

   <form  method="POST" id="contact" onSubmit={this.SendMail}>
   <span>
       <p>Nombre</p>
       <input type="text" placeholder="Juan Carlos" name="nombre" className="nombre" required/>
     </span>



     <span>
       <p>Apellidos</p>
       <input type="text" placeholder="Ramirez" name="apellido" className="apellido" required/>
     </span>

     <span>
       <p>Correo</p>
       <input type="email" placeholder="Nestormyriee@gmail.com" name="correo" className="correo " required/>
     </span>
     <span>
       <p>Numero</p>
       <input type="text"   name="numero" className="numero" placeholder='opcional'/>
     </span>

     <span className="mensajecontent" required>
       <p>Mensaje</p>
       <textarea  className='mensaje'name="mensaje"  placeholder="Cuentame Tu priyecto (Opcional)"></textarea>
     </span>
     <input type='submit' value="Enviar" className="enviar" />
   </form>
 </div>
 <div>
   <img src={img1} alt=""/>
   <div class="anclas">
   <a href='https://discord.gg/TkMRSwuqMT' target='_blank'>   <img src={discord} alt=""/></a>
     <a href='mailto:nestormyriee@gmail.com' target='_blank'><img src={gmail} alt=""/></a>
            <a href='https://www.facebook.com/nestor.myrie.3' target="_blank">
     <img src={facebook} alt=""/>

            </a>
   </div>
 </div>
<ExampleComponent/>
 <div className='success'  style={close1} onClick={this.rem}>
 <i className="far fa-times-circle close" ></i>
